// THIS IS SOME DUMMY DATA TO USE UNTIL CONNECTING TO THE DATABASE
// import images from "../../../images/black.jpeg"

// Importing material ui libraries
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
// import NavBarWorkspace from './NavBarWorkSpace';

// Importing required react libraries
import * as React from 'react';
import { Link } from 'react-router-dom';

// Linking to CSS files
// import '../../../CSS/boards.css';

// Page contents
function Boards() {
    const dummyData = [
        {
            id: 1,
            board_title: "Board 1",
            boardDescription: "This is Board 1"
        },
        {
            id: 2,
            board_title: "Board 2",
            boardDescription: "This is Board 2"
        },
        {
            id: 3,
            board_title: "Board 3",
            boardDescription: "This is Board 3"
        }
    ];

    const deleteBoard = (title) => {
        console.log("Deleted board:", title);
        // Add your logic to delete the board if needed
    };

    return (
        <div>
            <div id="boards-list-page">

                <div id="boardCards">
                    {/* THIS LOOPS THROUGH EVERY CARD */}
                    {dummyData.map((item) => (
                        <Card id="boardCard" key={item.id}>
                            {/* CARD IMAGE */}

                            {/* TEXTS OF EACH CARD */}
                            <CardContent>
                                <Typography gutterBottom variant="h5" component="div">
                                    {item.board_title}
                                </Typography>
                                <Typography variant="body2" color="text.secondary">
                                    {item.boardDescription}
                                </Typography>
                                <button
                                    type="button"
                                    onClick={() => deleteBoard(item.board_title)}
                                    className="btn-close"
                                    aria-label="Close"
                                ></button>
                            </CardContent>
                        </Card>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Boards;
