import React, { useState } from 'react';
// import IconButton from "@mui/material/IconButton";
import {Button, Grid, Paper, TextField, Typography} from "@mui/material";

function Board() {
    const [boardName, setBoardName] = useState('');
    const [boards, setBoards] = useState([]);
    const handleCreateBoard = () => {
        // Check if the board name already exists
        const isDuplicateName = boards.some((board) => board.name === boardName);

        if (isDuplicateName) {
            // Show an alert if the name is a duplicate
            alert("Board with the same name already exists!");
        } else {
            // Create a new board with the provided name
            const newBoard = {
                name: boardName,
                id: Date.now(), // Generate a unique ID for the board
            };

            // Add the new board to the list of boards
            setBoards([...boards, newBoard]);

            // Reset the board name input
            setBoardName('');
        }
    };
    const handleDeleteBoard = (id) => {
        // Filter out the board with the provided ID
        const updatedBoards = boards.filter((board) => board.id !== id);

        // Update the list of boards
        setBoards(updatedBoards);
    };

    return (
        <div style={{ padding: 16 }}>
            <Typography variant="h4" gutterBottom>
                Create a Board
            </Typography>
            <Grid container spacing={2} alignItems="center">
                <Grid item xs={8}>
                    <TextField
                        label="Board Name"
                        variant="outlined"
                        fullWidth
                        value={boardName}
                        onChange={(e) => setBoardName(e.target.value)}
                    />
                </Grid>
                <Grid item xs={4}>
                    <Button variant="contained" color="primary" onClick={handleCreateBoard}>
                        Create
                    </Button>
                </Grid>
            </Grid>

            <Typography variant="h4" gutterBottom>
                Boards
            </Typography>
            {boards.map((board) => (
                <Paper key={board.id} elevation={2} style={{ padding: 16, margin: '16px 0' }}>
                    <Grid container alignItems="center">
                        <Grid item xs={10}>
                            <Typography variant="h6">{board.name}</Typography>
                        </Grid>
                        <Grid item xs={2}>
                            <Button
                                variant="outlined"
                                color="secondary"
                                startIcon={<i className="fas fa-trash-alt"></i>} // FontAwesome trash icon
                                onClick={() => handleDeleteBoard(board.id)}
                            >
                                Delete
                            </Button>
                        </Grid>
                    </Grid>
                </Paper>
            ))}
        </div>
    );
}

export default Board;

