import React, { useState } from 'react';
// import IconButton from "@mui/material/IconButton";
import { Grid, Typography, TextField, Button, Paper } from '@mui/material';

function Board() {
    const [boardName, setBoardName] = useState('');
    const [workspaceName, setWorkspaceName] = useState('');
    const [boards, setBoards] = useState([]);

    useEffect(() => {
        fetch('http://localhost:8080/boards/getAllBoards')
            .then(response => response.json())
            .then(data => setBoards(data))
            .catch(error => console.error('Error:', error));
    }, []);

    const handleCreateBoard = () => {
        const newBoard = {
            boardName: boardName,
            workspaceName: workspaceName,
            picture: 'https://w7.pngwing.com/pngs/429/972/png-transparent-green-chalk-board-cartoon-blackboard-cartoon-green-chalkboard-miscellaneous-cartoon-character-english.png',
        };

        fetch('http://localhost:8080/boards/createBoard', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newBoard),
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                setBoards([...boards, data]);
                setBoardName('');
                setWorkspaceName('');
            })
            .catch(error => {
                console.error(error);
            });
    };

    const handleDeleteBoard = (boardName) => {
        fetch(`http://localhost:8080/boards/deleteBoard?boardName=${boardName}`, {
            method: 'POST',
        })
            .then(response => response.text())
            .then(data => {
                if (data === 'Board Successfully Deleted') {
                    console.log(data);
                    setBoards(prevBoards => prevBoards.filter(board => board.boardName !== boardName));
                } else {
                    console.log('Failed to delete board.');
                }
            })
            .catch(error => {
                console.error(error);
            });
    };

    return (
        <div style={{ padding: 16 }}>
            <Typography variant="h4" gutterBottom>
                Create a Board
            </Typography>
            <Grid container spacing={2} alignItems="center">
                <Grid item xs={6}>
                    <TextField
                        label="Board Name"
                        variant="outlined"
                        fullWidth
                        value={boardName}
                        onChange={(e) => setBoardName(e.target.value)}
                    />
                </Grid>
                <Grid item xs={6}>
                    <TextField
                        label="Workspace Name"
                        variant="outlined"
                        fullWidth
                        value={workspaceName}
                        onChange={(e) => setWorkspaceName(e.target.value)}
                    />
                </Grid>
                <Grid item xs={12}>
                    <Button variant="contained" color="primary" onClick={handleCreateBoard}>
                        Create
                    </Button>
                </Grid>
            </Grid>

            <Typography variant="h4" gutterBottom>
                Boards
            </Typography>
            {boards.map(board => (
                <Paper key={board.id} elevation={2} style={{ padding: 16, margin: '16px 0' }}>
                    <Grid container alignItems="center">
                        <Grid item xs={10}>
                            <img
                                src={board.picture}
                                alt={board.boardName}
                                style={{ width: 50, height: 50, marginRight: 10 }}
                            />
                            <Typography variant="h6">{board.boardName}</Typography>
                            <Typography variant="body2" color="text.secondary">
                                Workspace: {board.workspaceName}
                            </Typography>
                        </Grid>
                        <Grid item xs={2}>
                            <Button
                                variant="outlined"
                                color="secondary"
                                startIcon={<i className="fas fa-trash-alt"></i>} // FontAwesome trash icon
                                onClick={() => handleDeleteBoard(board.boardName)}
                                style={{ marginLeft: 'auto' }}
                            >
                                Delete
                            </Button>
                        </Grid>
                    </Grid>
                </Paper>
            ))}
        </div>
    );
}

export default Board;

