import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { TextField, Button, Container, Typography } from '@mui/material';



const WorkspaceEdit = () => {
    const { id } = useParams();
    const [workspace, setWorkspace] = useState({
        workspaceName: '',
        workspaceType: '',
        description: ''
    });



    useEffect(() => {
        // Fetch workspace data from the backend and update the state
        fetch(`http://localhost:8080/getWorkspaceById/${id}`)
            .then(response => response.json())
            .then(data => {
                if (data.status === 'success') {
                    setWorkspace(data.data);
                } else {
                    console.error('Error:', data.message);
                }
            })
            .catch(error => console.error('Error:', error));
    }, [id]);



    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setWorkspace(prevState => ({ ...prevState, [name]: value }));
    };



    const handleSubmit = (e) => {
        e.preventDefault();
        // Update workspace data in the backend
        fetch(`http://localhost:8080/updateWorkspace/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(workspace)
        })
            .then(response => response.json())
            .then(data => {
                if (data.status === 'success') {
                    console.log('Workspace updated successfully:', data.data);
                    window.location.href = '/workspaces'; // Navigate to the workspace list page
                } else {
                    console.error('Error:', data.message);
                }
            })
            .catch(error => console.error('Error:', error));
    };



    return (
        <Container maxWidth="sm">
            <Typography variant="h4" component="h1" align="center">
                Edit Workspace
            </Typography>
            <form onSubmit={handleSubmit}>
                <TextField
                    name="workspaceName"
                    label="Workspace Name"
                    value={workspace.workspaceName}
                    onChange={handleInputChange}
                    fullWidth
                    margin="normal"
                />
                <TextField
                    name="workspaceType"
                    label="Workspace Type"
                    value={workspace.workspaceType}
                    onChange={handleInputChange}
                    fullWidth
                    margin="normal"
                />
                <TextField
                    name="description"
                    label="Description"
                    value={workspace.description}
                    onChange={handleInputChange}
                    fullWidth
                    margin="normal"
                />
                <Button type="submit" variant="contained" color="primary">
                    Save
                </Button>
            </form>
        </Container>
    );
};



export default WorkspaceEdit;
