import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function WorkspaceEdit() {
    const { workspaceID } = useParams();
    const [workspace, setWorkspace] = useState(null);
    const [description, setDescription] = useState('');

    useEffect(() => {
        fetchWorkspace();
    }, []);

    const fetchWorkspace = async () => {
        try {
            const response = await fetch(`http://localhost:8080/getWorkspace/${workspaceID}`);
            const data = await response.json();
            setWorkspace(data.data);
            setDescription(data.data.description);
        } catch (error) {
            console.log('Error fetching workspace:', error);
        }
    };

    const handleSaveDescription = async () => {
        try {
            await fetch(`http://localhost:8080/updateWorkspace/${workspaceID}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ description }),
            });
            console.log('Workspace description updated successfully.');
        } catch (error) {
            console.log('Error updating workspace description:', error);
        }
    };

    const handleChangeDescription = (e) => {
        setDescription(e.target.value);
    };

    if (!workspace) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>Edit Workspace</h1>
            <h2>{workspace.workspaceName}</h2>
            <textarea value={description} onChange={handleChangeDescription} />
            <button onClick={handleSaveDescription}>Save Description</button>
        </div>
    );
}

export default WorkspaceEdit;
