// Importing required react libraries
import { Button, TextField, Typography } from '@mui/material';

// Importing required react libraries
import React, { useRef } from 'react';

// // Linking to CSS files
// import "../../../CSS/createBoardForm.css"
// import "../../../CSS/create-new-workspace-form.css"

// Page content
function CreateBoardForm(props) {

    // Functions required
    const boardNameRef = useRef();
    const boardDespRef = useRef();

    function createBoard(e) {
        e.preventDefault();
        const boardName = boardNameRef.current.value;
        const boardDesp = boardDespRef.current.value;

        const board = {
            'board_title': boardName,
            'workspace_name': boardDesp
        };

        props.createBoard(board);
    };

    return (
        <section style={{ marginTop: '32px' }} id='createBoardFormSession' >
            <Typography variant='h2' component='h2'>Create New Board</Typography>
            <form onSubmit={createBoard}>

                <div id="create-new-board-form">
                    <TextField id="boardName" required label="Board Name" type="search" inputRef={boardNameRef}/>
                    <TextField id="boardDesp" required label="Board Description" type="search" multiline={true} rows={10} inputRef={boardDespRef}/>
                </div>

                <Button type='submit' variant='contained' color='primary' sx={{ marginTop: '16px' }}>
                    Create Board
                </Button>
            </form>
        </section>
    );
};

export default CreateBoardForm;
