import {Button, TextField, Typography} from "@mui/material";
import {Field, Form, Formik} from "formik";
import {useDispatch} from "react-redux";
import {useNavigate} from "react-router-dom";
import navigation from "./Navigation";
import {useEffect, useState} from "react";

export default function WorkspaceCreateForm() {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [workspaceName, setWorkspaceName] = useState('');
    const [workspaceDescription, setWorkspaceDescription] = useState('');

    useEffect(() => {
        fetch('http://localhost:8080/workspaces/getAllWorkspaces')
            .then(response => response.json())
            .then(data => setWorkspaceName(data))
            .catch(error => console.error('Error:', error));
    }, []);

    const handleCreateWorkspace = () => {
        if (!workspaceDescription || !workspaceName) {
            alert('Please enter both workspace description and workspace name.');
            return;
        }
        const newWorkspace = {
            workspaceDescription: workspaceDescription,
            workspaceName: workspaceName,
            picture: 'https://w7.pngwing.com/pngs/429/972/png-transparent-green-chalk-board-cartoon-blackboard-cartoon-green-chalkboard-miscellaneous-cartoon-character-english.png',
        };

