            <Form>
                <div>
                    <h3>Workspace Name:</h3>
                </div>
                <div>
                    <Field
                        as={TextField}
                        name="name"
                        label="Name"
                        variant="outlined"

                    />
                </div>
                <div>
                    <h3>
                        Description of Workspace:
                    </h3>
                </div>
                <div>
                    <Field
                        as={TextField}
                        name="description"
                        label="Description"
                        variant="outlined"

                    />
                </div>
                <br/>
                <div>
                    <Button type="submit" variant="contained" color="primary">
                        Create Workspace
                    </Button>
                </div>
            </Form>
        </Formik>
    )
}
