import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { TextField, Button, Typography, Container, Snackbar } from '@mui/material';

const WorkspaceEdit = () => {
    const { id } = 103;
    const [workspace, setWorkspace] = useState({
        workspaceName: '',
        workspaceType: '',
        description: ''
    });
    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');

    useEffect(() => {
        // Fetch workspace data from the backend
        fetch(`http://localhost:8080/getWorkspaceById/${id}`)
            .then(response => response.json())
            .then(data => {
                if (data.status === 'success') {
                    setWorkspace(data.data);
                } else {
                    console.error('Failed to fetch workspace:', data.message);
                }
            })
            .catch(error => {
                console.error('Error:', error);
            });
    }, [id]);

    const handleInputChange = e => {
        const { name, value } = e.target;
        setWorkspace(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleFormSubmit = e => {
        e.preventDefault();
        // Update the workspace in the backend
        fetch(`http://localhost:8080/updateWorkspace/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(workspace)
        })
            .then(response => response.json())
            .then(data => {
                if (data.status === 'success') {
                    setSnackbarMessage('Workspace updated successfully');
                    setSnackbarOpen(true);
                } else {
                    setSnackbarMessage('Failed to update workspace');
                    setSnackbarOpen(true);
                    console.error('Failed to update workspace:', data.message);
                }
            })
            .catch(error => {
                setSnackbarMessage('Error updating workspace');
                setSnackbarOpen(true);
                console.error('Error:', error);
            });
    };

    const handleSnackbarClose = () => {
        setSnackbarOpen(false);
    };

    return (
        <Container maxWidth="sm">
            <Typography variant="h4" component="h1" align="center">
                Edit Workspace
            </Typography>
            <form onSubmit={handleFormSubmit}>
                <TextField
                    name="workspaceName"
                    label="Workspace Name"
                    value={workspace.workspaceName}
                    onChange={handleInputChange}
                    fullWidth
                    required
                />
                <TextField
                    name="workspaceType"
                    label="Workspace Type"
                    value={workspace.workspaceType}
                    onChange={handleInputChange}
                    fullWidth
                />
                <TextField
                    name="description"
                    label="Description"
                    value={workspace.description}
                    onChange={handleInputChange}
                    multiline
                    fullWidth
                />
                <Button type="submit" variant="contained" color="primary" fullWidth>
                    Update
                </Button>
            </form>
            <Snackbar
                open={snackbarOpen}
                autoHideDuration={3000}
                onClose={handleSnackbarClose}
                message={snackbarMessage}
            />
        </Container>
    );
};

export default WorkspaceEdit;
