import React, { useEffect, useState } from 'react';
import { Card, CardContent, Typography, Button } from '@mui/material';
import { useHistory } from 'react-router-dom';



function WorkspaceList() {
    const [workspaces, setWorkspaces] = useState([]);
    const history = useHistory();



    useEffect(() => {
        fetchWorkspaces();
    }, []);



    const fetchWorkspaces = async () => {
        try {
            const response = await fetch('http://localhost:8080/getAllWorkspaces');
            const data = await response.json();
            setWorkspaces(data.data);
        } catch (error) {
            console.log('Error fetching workspaces:', error);
        }
    };



    const handleViewBoards = async (workspaceId) => {
        try {
            const response = await fetch(`http://localhost:8080/boards/getBoardsByWorkspace?workspaceId=${workspaceId}`);
            const data = await response.json();
            const updatedWorkspaces = workspaces.map(workspace => {
                if (workspace.id === workspaceId) {
                    return {
                        ...workspace,
                        boards: data
                    };
                }
                return workspace;
            });
            setWorkspaces(updatedWorkspaces);
        } catch (error) {
            console.log('Error fetching boards:', error);
        }
    };



    const handleCreateBoard = (workspaceId) => {
        history.push(`/create-board/${workspaceId}`);
    };



    return (
        <div>
            <h1>Workspace List</h1>
            {workspaces.map(workspace => (
                <Card key={workspace.id} variant="outlined" sx={{ mb: 2 }}>
                    <CardContent>
                        <Typography variant="h5" component="div">
                            {workspace.workspaceName}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                            {workspace.description}
                        </Typography>
                        <Button onClick={() => handleViewBoards(workspace.id)} variant="outlined">
                            View Boards
                        </Button>
                        {workspace.boards && workspace.boards.length > 0 && (
                            <div>
                                <h3>Boards:</h3>
                                <ul>
                                    {workspace.boards.map(board => (
                                        <li key={board.id}>{board.boardName}</li>
                                    ))}
                                </ul>
                            </div>
                        )}
                        <Button onClick={() => handleCreateBoard(workspace.id)} variant="contained" color="primary">
                            Create Board
                        </Button>
                    </CardContent>
                </Card>
            ))}
        </div>
    );
}



export default WorkspaceList;
